import {useEffect, useState} from 'react';
import axios from 'axios';

const useAxios = (url) => {
    const [response,setResponse] = useState([]);

    const addItem = async () => {
        const res = await axios.get(url);
        setResponse(r => [...r,res])
    }

    useEffect(() => {
        const fetchData = async () =>{
            try {
                const res = await axios.get(url);
                setResponse(r => [...r,res])
            }
            catch(e){

            }
        }
        fetchData()
    },[])


    return [response,addItem]
}

export default useAxios;
