import React, { useState } from "react";
import { v4 as uuid } from 'uuid';
import axios from "axios";
import PlayingCard from "./PlayingCard";
import "./PlayingCardList.css";
import useAxios from "./hooks/useAxios";

/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {
  const [cards, setCards] = useState([]);
  const [response,addItem] = useAxios("https://deckofcardsapi.com/api/deck/new/draw/")


  // const addCard = async () => {
  //   // const response = await axios.get(
  //   //   "https://deckofcardsapi.com/api/deck/new/draw/"
  //   // );

  //   setCards(cards => [...cards, { ...response.data, id: uuid() }]);
  // };
  console.log(response.map(m => console.log(m.data)))
  return (
    <div className="PlayingCardList">
      <h3>Pick a card, any card!</h3>
      <div>
        <button onClick={addItem}>Add a playing card!</button>
      </div>
      <div className="PlayingCardList-card-area">
        {response.map(cardData => (
          <PlayingCard key={cardData.data.deck_id} front={cardData.data.cards[0].image} />
        ))}
      </div>
    </div>
  );
}

CardTable.defaultProps = {};

export default CardTable;
